var ip = "192.168.0.54";
var loginName = null;
var pageNum = 1;

function getSubjectGroup() {
	$.ajax({
		type: "GET",
		url: "http://" + ip + "/bcat/thematicGroup/groupPage.do?pageNo=" + pageNum + "&jsonp=?",
		dataType: "jsonp",
		success: function(c) {
			var a = "";
			var b = "";
			sessionStorage.setItem("totalPage", c.result.totalPage);
			$.each(c.result.privateLetteList, function(f, g) {
				a = getStatus(g.groupStatus);
				var d = parseInt(c.result.pageNo);
				var e = parseInt(f);
				if(g.fbStatus === "0") {}
				$("#DataTables_Table_0_info span:first-child").html((d - 2) * 10 + e + 2);
				$("#DataTables_Table_0_info span:last-child").html((d - 2) * 10 + e + 11)
			});
			$("#tbody").html(b);
			$("#totalRecord").html(c.result.totalRecord)
		}
	})
}
getSubjectGroup();

